    constructor(props: {}){
        super(props)
    }



        render(){
            return(
                <CartConsumer>
                    {({ getTotalPrice, selectedShipping }) => (
                        <div>
                            <h2>Total price including shipping & VAT:</h2> 

                            <h2> {getTotalPrice() + selectedShipping.cost} :- </h2>
                            <p> VAT: {(getTotalPrice() + selectedShipping.cost) * 0.2} :- </p>
                        </div>
                    )}

                </CartConsumer>
            )
        }
    }




export default TotalPrice;
